let isCliMode = false;
let commandHistory = [];
let historyIndex = -1;

// Typing Bio Animation
async function typeBio() {
    const bioElement = document.getElementById('typingBio');
    if (!bioElement) return;

    const bioText = [
        "Hello friend, Who am I? I am:",
        "A Jr. Pentester Student & IT Security Research Student"
    ];

    bioElement.innerHTML = '';

    for (let i = 0; i < bioText.length; i++) {
        const line = document.createElement('div');
        line.className = 'bio-line';
        bioElement.appendChild(line);

        let currentText = '';
        const textToType = bioText[i];
        for (let j = 0; j < textToType.length; j++) {
            currentText += textToType[j];
            line.textContent = currentText;
            if (j === textToType.length - 1) {
                const cursor = document.createElement('span');
                cursor.className = 'typing-cursor';
                line.appendChild(cursor);
            }
            await new Promise(r => setTimeout(r, 40));
        }
        if (i < bioText.length - 1) {
            const cursor = line.querySelector('.typing-cursor');
            if (cursor) cursor.remove();
            await new Promise(r => setTimeout(r, 600));
        }
    }
}

// Section Toggling
function toggleSection(sectionId) {
    const sections = ['projects', 'certificates', 'ctf', 'contact'];
    sections.forEach(id => {
        const sec = document.getElementById(id + 'Section');
        if (sec) {
            if (id === sectionId) {
                sec.classList.add('visible');
                sec.classList.remove('hidden');
            } else {
                sec.classList.remove('visible');
                sec.classList.add('hidden');
            }
        }
    });
    document.querySelectorAll('.social-links .gruv-button').forEach(btn => {
        const onclick = btn.getAttribute('onclick') || '';
        btn.classList.toggle('active', onclick.includes(`toggleSection('${sectionId}')`));
    });
}

// Time-based Greeting
function getGreeting() {
    const hour = new Date().getHours();
    if (hour >= 5 && hour < 12) return 'Good Morning';
    if (hour >= 12 && hour < 17) return 'Good Afternoon';
    if (hour >= 17 && hour < 21) return 'Good Evening';
    return 'Good Night';
}

// Terminal Updates
function updateTerminal() {
    const terminal = document.getElementById('visitorInfo');
    if (!terminal) return;
    const greeting = getGreeting();
    const randomIP = Array.from({ length: 4 }, () => Math.floor(Math.random() * 256)).join('.');
    terminal.innerHTML = `
        <div class="terminal-line"><span class="prompt">$</span> ${greeting}, Welcome to 0x1C's portfolio</div>
        <div class="terminal-line"><span class="prompt">$</span> Connection established... IP: ${randomIP}</div>
        <div class="terminal-line"><span class="prompt">$</span> User Agent: ${navigator.userAgent.substring(0, 40)}...</div>
        <div class="terminal-line"><span class="prompt">$</span> Status: <span class="gruv-green">Secure & Ready</span></div>
        <div class="terminal-line"><span class="prompt">$</span> Type <span class="gruv-yellow">'help'</span> in CLI (<i class="fas fa-terminal"></i>) for commands.</div>
    `;
}

// Load Projects from data/repos.json
async function loadProjects() {
    const container = document.getElementById('github-projects');
    if (!container) return;
    container.innerHTML = '<div class="terminal-line loading">Loading projects...</div>';
    try {
        const resp = await fetch('/data/repos.json');
        if (!resp.ok) throw new Error(resp.statusText);
        const repos = await resp.json();
        container.innerHTML = '';
        repos.forEach((repo, idx) => {
            const card = document.createElement('div');
            const category = (repo.language || '').toLowerCase().replace(/[^a-z0-9]/g, '-');
            card.className = `project-card ${category}`;
            card.style.animationDelay = `${idx * 0.07}s`;
            card.innerHTML = `
                <h3 class="project-title"><a href="${repo.html_url}" target="_blank">${repo.name}</a></h3>
                <p class="project-description-github">${repo.description || 'No description available.'}</p>
                ${repo.language ? `<p class="project-info"><strong>Language:</strong> ${repo.language}</p>` : ''}
                <div class="project-stats">
                    <span><i class="fas fa-star"></i> ${repo.stargazers_count}</span>
                    <span><i class="fas fa-code-branch"></i> ${repo.forks_count}</span>
                    <span><i class="fas fa-eye"></i> ${repo.watchers_count}</span>
                </div>
                <a href="${repo.html_url}" target="_blank" class="gruv-button"><i class="fab fa-github"></i> View on GitHub</a>
            `;
            container.appendChild(card);
        });
    } catch (err) {
        console.error('Project load error:', err);
        container.innerHTML = `<div class="terminal-line gruv-red">Error loading projects: ${err.message}</div>`;
    }
}

// Load CTF Challenges
function loadCTFChallenges() {
    const ctfContainer = document.getElementById('ctf-challenges');
    if (!ctfContainer) return;
    ctfContainer.innerHTML = '';
    const badgeHTML = `
        <div class="ctf-badge-container">
            <iframe src="https://tryhackme.com/api/v2/badges/public-profile?userPublicId=4739468" style='border:none;width:100%;height:300px;' loading="lazy"></iframe>
        </div>
    `;
    const card = document.createElement('div');
    card.className = 'ctf-card';
    card.innerHTML = `
        <h4 class="ctf-title">TryHackMe Profile</h4>
        <p class="project-description" style="margin-bottom:1rem;">My public TryHackMe badge showcasing activity and rank.</p>
        ${badgeHTML}
    `;
    ctfContainer.appendChild(card);
}

