// CLI Mode Toggle
function toggleCliMode() {
    isCliMode = !isCliMode;
    document.querySelector('.container').classList.toggle('hidden', isCliMode);
    document.getElementById('cliContainer').classList.toggle('hidden', !isCliMode);
    document.getElementById('bunnyTrigger').classList.toggle('hidden', isCliMode);
    if (isCliMode) {
        printCliWelcome();
        cliCommandInput.focus();
    }
}

function printCliWelcome() {
    if (!cliOutput) return;
    cliOutput.innerHTML = `
        <div class="terminal-line gruv-purple">0x1C Portfolio CLI v0.1</div>
        Type <span class="gruv-yellow">help</span> for commands.
    `;
}

function printToCli(html, isCmd=false, cmdText='') {
    if (!cliOutput) return;
    if (isCmd) cliOutput.innerHTML += `<div class="terminal-line"><span class="prompt">$</span> ${cmdText}</div>`;
    cliOutput.innerHTML += `<div class="terminal-line">${html}</div>`;
    cliOutput.scrollTop = cliOutput.scrollHeight;
}

function handleCommand(input) {
    const parts = input.trim().split(/ +/);
    const cmd = parts[0];
    const args = parts.slice(1);
    printToCli('', true, input);
    if (cmd === 'exit') { toggleCliMode(); return; }
    const fn = cliCommands[cmd];
    const res = fn ? fn(args) : `<span class="gruv-red">Unknown command:</span> ${cmd}`;
    if (res) printToCli(res);
}

// Init
const cliOutput = document.getElementById('cliOutput');
const cliCommandInput = document.getElementById('cliCommand');
document.addEventListener('DOMContentLoaded', () => {
    updateTerminal();
    typeBio();
    loadProjects();
    loadCTFChallenges();

    // CLI input
    cliCommandInput.addEventListener('keypress', e => {
        if (e.key === 'Enter') {
            const val = e.target.value;
            if (val && (commandHistory[commandHistory.length-1] !== val)) commandHistory.push(val);
            historyIndex = commandHistory.length;
            handleCommand(val);
            e.target.value = '';
        }
    });
    cliCommandInput.addEventListener('keydown', e => {
        if (e.key === 'ArrowUp') { if (historyIndex>0) { historyIndex--; cliCommandInput.value = commandHistory[historyIndex]; }}
        if (e.key === 'ArrowDown') { if (historyIndex<commandHistory.length-1) { historyIndex++; cliCommandInput.value = commandHistory[historyIndex]; } else { historyIndex = commandHistory.length; cliCommandInput.value = ''; }}
    });

    document.getElementById('bunnyTrigger').addEventListener('click', toggleCliMode);
    document.getElementById('themeToggle').addEventListener('click', () => {
        document.body.classList.toggle('light-theme');
        document.getElementById('themeIcon').classList.toggle('fa-sun');
    });
    document.getElementById('scrollToTopBtn').addEventListener('click', () => window.scrollTo({top:0,behavior:'smooth'}));

    toggleSection('projects');
});

